mapper.select:
SELECT * FROM tb_student_grade
WHERE student_id IN

    #{id}

AND course IN

    #{name}

ORDER BY student_id ASC

SELECT * FROM tb_student_grade
WHERE student_id = #{studentId}
AND course IN

    #{name}

ORDER BY student_id ASC

mapper.delete:
delete from tb_student_grade where course = #{course} and semester = #{semester} and school_year = #{schoolYear} and
student_id in

    #{studentId}
